sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   //Type appropriate comment here, and begin script below
	var ga = new GlideAjax("PullCRDetails");
	ga.addParam('sysparm_name', 'getCRDetails');
	ga.addParam('sysparm_uChangeNumber', newValue);
	ga.getXML(CRcallBackFunction);

	function durationCallBackFunction(response) {
		var answer = response.responseXML.documentElement.getAttribute('answer');
		g_form.setValue('se_hours_consumed', answer);
	}	

	function UsercallBackFunction(response) {
		var answer = response.responseXML.documentElement.getAttribute('answer');
		var v = [];
		var a = answer.split(",");
		v.push(a[0]);
		v.push(a[1]);
		g_form.setValue('primary_se', v[1] + ", " + v[0]);
	}	

	function CRcallBackFunction(response) {
		var answer = response.responseXML.documentElement.getAttribute('answer');
		var v = [];
		var a = answer.split(",");
		v.push(a[0]);
		v.push(a[1]);
		v.push(a[2]);
		v.push(a[3]);
		v.push(a[4]);
		v.push(a[5]);
		v.push(a[6]);
		g_form.setValue('cr_start_time', v[0]);
		g_form.setValue('cr_end_time', v[1]);
		g_form.setValue('risk', v[2]);
		g_form.setValue('status', v[3]);
		g_form.setValue('change_description', v[5]);
		g_form.setValue('scope_of_work', v[6]);

		var ga1 = new GlideAjax("PullUserNameDetails");
		ga1.addParam('sysparm_name', 'getUserDetails');
		ga1.addParam('sysparm_uname', v[4]);
		ga1.getXML(UsercallBackFunction);

		var ga2 = new GlideAjax("getTimeDifference");
		ga2.addParam('sysparm_name', 'timeDifference');
		ga2.addParam('sysparm_start', g_form.getValue("cr_start_time"));
		ga2.addParam('sysparm_end', g_form.getValue("cr_end_time"));
		ga2.getXML(durationCallBackFunction);


	}

}
