sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   //Type appropriate comment here, and begin script below
	var ga = new GlideAjax("PullCRDetails");
	ga.addParam('sysparm_name', 'getCRDetails');
	ga.addParam('sysparm_uChangeNumber', newValue);
	ga.getXML(callBackFunction);

	function callBackFunction(response) {
		var answer = response.responseXML.documentElement.getAttribute('answer');
		var v = [];
		var a = answer.split(",");
		v.push(a[0]);
		v.push(a[1]);
		v.push(a[6]);
		g_form.setValue('change_start_time', v[0]);
		g_form.setValue('change_finish_time', v[1]);
		g_form.setValue('description_of_activity', v[2]);
	}

}
