sys_script_include.script:
var PullCRDetails = Class.create();
PullCRDetails.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getCRDetails: function() {
		var gr = new GlideRecord("change_request");
		gr.get(this.getParameter("sysparm_uChangeNumber"));
		gr.query();
		if (gr.next()) {
			var gc = [gr.getValue("work_start"),
						gr.getValue("work_end"),
						gr.getValue("risk"),
						gr.getValue("state"),
						gr.getValue("assigned_to"),
						gr.getValue("short_description"),
						gr.getValue("description"),
			];
			return gc.toString();
		}
	},

    type: 'PullCRDetails'
});
