sys_ui_policy.script_false:
function onCondition() {

	if (g_form.getValue("comp_off_days") > g_form.getValue("se_change_hours_consumed")){
		g_form.setValue("comp_off_hours_pending", g_form.getValue("comp_off_days") - g_form.getValue("se_change_hours_consumed"));
		g_form.setValue("comp_off_status", "Partially Taken");
		jslog("condition 2-1 ");
	} else {
		g_form.setValue("comp_off_hours_pending", 0);
		g_form.setValue("comp_off_status", "New");	
		jslog("condition 2-2 ");
	}
}

sys_ui_policy.script_true:
function onCondition() {
	if (g_form.getValue("comp_off_days") <= g_form.getValue("se_change_hours_consumed")){
		g_form.setValue("comp_off_hours_pending", 0);
		g_form.setValue("comp_off_status", "Taken");
		jslog("condition 1 ");
	}

	if (g_form.getValue("se_change_hours_consumed") == 0 || g_form.getValue("se_change_hours_consumed") == '' ){
		g_form.setValue("comp_off_hours_pending", g_form.getValue("x_486042_se_pjt_tr_comp_off"));
		g_form.setValue("comp_off_status", "Pending");
		jslog("condition 2 ");
	}

	if (g_form.getValue("comp_off_days") > g_form.getValue("se_change_hours_consumed")){
		g_form.setValue("comp_off_hours_pending", g_form.getValue("x_486042_se_pjt_tr_comp_off") - g_form.getValue("se_change_hours_consumed"));
		g_form.setValue("comp_off_status", "Partially Taken");
		jslog("condition 3 ");
	}


}
